from collections import Counter

class Solution:
    def longestPalindrome(self, s):
        total = 0
        has_odd = False
        for val in Counter(s).values():
            if val % 2 == 0:
                total += val
            else:
                total += val - 1
                has_odd = True

        if has_odd:
            total += 1

        return total


s = ""
print(Solution().longestPalindrome(s))
